const INTRO_BANNER = [
  '<!-- vm-banner-start -->',
  `ğŸ”§ **Version Management ìë™ ë³€ê²½ ì´ë ¥**`,
  '',
  `ì´ íŒŒì¼ì€ ì¤‘ì•™ ë°°í¬ ì›Œí¬í”Œë¡œ(**Version Management**)ê°€ ìë™ ìƒì„±Â·ìœ ì§€í•©ë‹ˆë‹¤.`,
  'This file is automatically generated and maintained by the centralized workflow (**Version Management**).',
  `ì œì‘ì(Author): **${BANNER_AUTHOR_NAME}** Â· ${BANNER_AUTHOR_URL}`,
  `ì›Œí¬í”Œë¡œ ì €ì¥ì†Œ(Workflow repository): ${BANNER_WORKFLOW_URL}`,
  '',
  'â€» ì ˆëŒ€ë¡œ ì´ íŒŒì¼ì„ ì„ì˜ë¡œ ìˆ˜ì •í•˜ì§€ ë§ˆì„¸ìš”',
  'â€» Do not edit this file manually.',
  '<!-- vm-banner:end -->',
].join('\n');

// ìœ íš¨ì„± ê²€ì‚¬
if (!newVersion) {
  console.error("NEW_VERSION ì€ í•„ìˆ˜ë¡œ ì„¤ì •ë˜ì–´ì•¼ í•©ë‹ˆë‹¤.");
  process.exit(1);
}

if (!bumpLevel) {
  console.error("Version ì»¤ë°‹ ì‹œ (major | minor | patch) bumpLevel ì´ í•„ìˆ˜ë¡œ ì„¤ì •ë˜ì–´ì•¼í•©ë‹ˆë‹¤.");
  process.exit(1);
}

if (!commitSubject) {
  console.error("COMMIT_SUBJECT ê°€ ë¹„ì–´ìˆìŠµë‹ˆë‹¤.");
  process.exit(1);
}

// ë°°ë„ˆ/í—¤ë” ìœ í‹¸ ë©”ì„œë“œ
function splitByBanner(input) {
  // ë°°ë„ˆ ë¸”ë¡ì„ ì°¾ì•„ ë¶„ë¦¬
  const re = /<!--\s*vm-banner-start\s*-->[\s\S]*?<!--\s*vm-banner:end\s*-->/i;
  const m = input.match(re);
  if (!m) return { banner: '', rest: input };

  const banner = m[0];
  const before = input.slice(0, m.index).trim();
  const after  = input.slice(m.index + m[0].length).replace(/^\s+/, '');
  // ë°°ë„ˆ ì•ì˜ ì¡ë™ì‚¬ë‹ˆëŠ” ë²„ë¦¼, ë°°ë„ˆ ë’¤ë¶€í„°ê°€ rest
  return { banner, rest: after };
}

function stripLeadingHeader(body) {
  // íŒŒì¼ ìƒë‹¨ì˜ H1 í—¤ë”(Changelog) 1ê°œë§Œ ì œê±°
  const reHeader = /^\s*#\s*(?:Chuseok22\s+)?Version\s+Changelog\s*\n?/i;
  return body.replace(reHeader, '').replace(/^\n+/, '');
}

function buildEntry({ version, level, subject, sha }) {
  const emoji = level === 'major' ? 'ğŸš€' : level === 'minor' ? 'âœ¨' : 'ğŸ›';
  const date  = new Date().toISOString().slice(0,10);
  const description  = subject.replace(/^\s*version\s*\(\s*(major|minor|patch)\s*\)\s*:\s*/i, '').trim();

  const lines = [];
  lines.push(`## [${version}] - ${date}`);
  lines.push('');
  lines.push(`${emoji} **${level}**: ${description}`);
  if (sha) lines.push(`- commit: \`${sha}\``);
  lines.push('');
  return lines.join('\n');
}

// ë³¸ë¬¸ ì¡°ë¦½ ë¡œì§
let prevRaw = '';
let fileExists = false;
if (fs.existsSync('CHANGELOG.md')) {
  prevRaw = normalizeEol(fs.readFileSync('CHANGELOG.md', 'utf8'));
  fileExists = true;
}

// ì´ì „ íŒŒì¼ì—ì„œ ë°°ë„ˆë¥¼ ë¶„ë¦¬ & ë³´ì¡´
let bannerBlock = INTRO_BANNER;
let previousBody = '';

if (fileExists && prevRaw.trim().length > 0) {
  const { banner, rest } = splitByBanner(prevRaw);
  if (banner) bannerBlock = normalizeEol(banner); // ê¸°ì¡´ ë°°ë„ˆ ìœ ì§€
  // ë°°ë„ˆ ë’¤ì— ë¶™ì–´ ìˆë˜ í—¤ë”ë¥¼ ì œê±°í•˜ê³ , ê·¸ ì´í•˜ë§Œ ì´ì „ ì´ë ¥ìœ¼ë¡œ ì‚¬ìš©
  previousBody = stripLeadingHeader(rest);
}

const { entry, description } = buildEntry({
  version: newVersion,
  level: bumpLevel.toLowerCase(),
  subject: commitSubject,
  sha: commitSha,
});

// ë‹¤ìŒ ìŠ¤í…(create-tag)ì—ì„œ ì¬ì‚¬ìš©í•˜ë„ë¡ ì¶œë ¥
setOutput('release_description', description);

// í•­ìƒ "ë°°ë„ˆ â†’ í—¤ë” â†’ ìµœì‹  ì—”íŠ¸ë¦¬ â†’ ì´ì „ ì´ë ¥" ìˆœì„œë¡œ ì¬ì¡°ë¦½
const parts = [];
parts.push(bannerBlock);
parts.push('');
parts.push(HEADER_NEW);
parts.push('');
parts.push(entry);

// ì´ì „ ì´ë ¥ì´ ìˆìœ¼ë©´ ì´ì–´ë¶™ì„ (ë°°ë„ˆ/í—¤ë”ëŠ” ì œê±°ëœ ìƒíƒœì˜ ë³¸ë¬¸ë§Œ)
if (previousBody && previousBody.trim().length > 0) {
  // ë‘ ê°œì˜ ë¹ˆ ì¤„ë¡œ êµ¬ë¶„(ê°€ë…ì„±)
  if (!previousBody.startsWith('\n')) parts.push('');
  parts.push(previousBody.replace(/^\n+/, ''));
}

const output = parts.join('\n').replace(/\n{3,}/g, '\n\n'); // ê³¼ë„í•œ ë¹ˆ ì¤„ ì •ë¦¬
fs.writeFileSync('CHANGELOG.md', output.endsWith('\n') ? output : output + '\n', 'utf8');

